import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Link,
} from '@mui/material';
import axios from 'axios';

const StatsPage = () => {
  const [stats, setStats] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await axios.get('/stats'); // backend endpoint
        setStats(res.data);
      } catch (err) {
        console.error('Error fetching statistics:', err);
      }
    };
    fetchStats();
  }, []);

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" mt={4} mb={2} align="center">
        URL Shortener Statistics
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Original URL</TableCell>
              <TableCell>Short URL</TableCell>
              <TableCell>Created At</TableCell>
              <TableCell>Expires At</TableCell>
              <TableCell>Click Count</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {stats.map((item, index) => (
              <TableRow key={index}>
                <TableCell>{item.originalUrl}</TableCell>
                <TableCell>
                  <Link href={item.shortUrl} target="_blank" rel="noopener">
                    {item.shortUrl}
                  </Link>
                </TableCell>
                <TableCell>{new Date(item.createdAt).toLocaleString()}</TableCell>
                <TableCell>{new Date(item.expiresAt).toLocaleString()}</TableCell>
                <TableCell>{item.clicks || 0}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default StatsPage;
