const logger = (message, level = 'info') => {
  const timestamp = new Date().toISOString();
  const logEntry = `[${timestamp}] [${level.toUpperCase()}] ${message}`;
  // Send to backend logger if needed (e.g., POST /log)
  // fetch('/log', { method: 'POST', body: JSON.stringify({ message: logEntry }) });

  // Store in localStorage or send to monitoring if needed
  localStorage.setItem('lastLog', logEntry); // optional for demo

  // Do not use console.log directly
};

export default logger;
